from xnt.basecommand import Command
from xnt.status_codes import SUCCESS

class VersionCommand(Command):
    name = 'version'
    usage = """"""
    summary = "Print Version of Xnt"
    needs_build = False

    def run(arguments=[]):
        from xnt import __version__
        print(__version__)

        return SUCCESS
