        (u'バ',       'ba'),
        (u'ビ',       'bi'),
        (u'ブ',       'bu'),
        (u'ベ',       'be'),
        (u'ボ',       'bo'),
        (u'パ',       'pa'),
        (u'ピ',       'pi'),
