        (u'ル',       'lu'),
        (u'レ',       'le'),
        (u'ロ',       'lo'),
        (u'ワ',       'wa'),
        (u'ヲ',       'o'),
        (u'ン',       'N'),
        (u'ッ',       'T'),
        ('^k',        'g'),
        ('^t',        'd'),
        ('^c{a|i}',   'z'),
        ('{(y|j)o}o', 'O'),
        ('{a}a',      None),
        ('{i}i',      None),
        ('{u|o}u',    None),
        ('{e}e',      None),
        ('{o}o',      None),
