def spacalc(dt, tz, location=(131.9, 43.1), elev=20, pres=1013.0, temp=20, slope=0):    
  zenith = spa_calc(dt.year, dt.month, dt.day, dt.hour, dt.minute, dt.second, tz, \
      location[1], location[0], elev, pres, temp, slope)
  H = 90.0 - zenith
  if H<0:
    H=0
  X = 1.0 / np.sin(np.deg2rad(H+244.0/(165+47*H**1.1)))
  return zenith, X

def spacalc1(dt, tz, location=(131.9, 43.1), elev=20, pres=1013.0, temp=20, slope=0):
  return spa_calc1(dt.year, dt.month, dt.day, dt.hour, dt.minute, dt.second, tz, \
      location[1], location[0], elev, pres, temp, slope)

